<?php    /*     * H-1 periode lelang berakhir.     * Data di store ke table notification untuk memudahkan dalam pengitimak ke user     */    // configuration file    require_once './config.php';        //Tanggal now indonesia    $date_now_id = date('Y-m-d H:i:s');    $date_now_id = strtotime('+1 days', strtotime($date_now_id));    $date_id_h1 = date('Y-m-d',$date_now_id);        $qry = "SELECT                *            FROM                items            WHERE                is_active=1                 AND is_finish=0                 AND DATE(end_date) = '".$date_id_h1."'	";    $q = mysql_query($qry);    $r = mysql_fetch_array($q);    if($r)    {        //Tanggal notifikasi harus dikirim ke user dan dimasukan ke inbox        $date_now = date('Y-m-d');        //Tanggal now indonesia        $date_now_id = date('Y-m-d H:i:s');        $date_now_id = strtotime('+7 hours', strtotime($date_now_id));        $date_now_id = date('Y-m-d H:i:s',$date_now_id);                $subject = "LELANG";        $meesage = "Last auction day, last chance to place the bid!";        $qry_u = "SELECT                    *                FROM                    user_accounts                ORDER BY created ASC                ";        $q_u = mysql_query($qry_u);                while($rs=mysql_fetch_array($q_u))        {            //Store ke notification            $sql_notification = "INSERT INTO `notification` "                    . "("                        . "`id`, "                        . "`user_id`, "                        . "`user_email`, "                        . "`user_name`, "                        . "`subject`, "                        . "`message`, "                        . "`send_date`, "                        . "`created`"                    . ") "                    . "VALUES "                    . "("                        . "NULL, "                        . "".$rs['id'].", "                        . "'".$rs['email']."', "                        . "'".$rs['name']."', "                        . "'".$subject."',"                        . "'".$meesage."',"                        . "'".$date_now."',"                        . "'".$date_now_id."'"                    . ");";            mysql_query($sql_notification);        }    }?>