<?php    // configuration file    require_once './config.php';    //Tanggal notifikasi harus dikirim ke user dan dimasukan ke inbox    $date_now = date('Y-m-d');        //Tanggal now indonesia    $date_now_id = date('Y-m-d H:i:s');    $date_now_id = strtotime('+7 hours', strtotime($date_now_id));    $date_now_id = date('Y-m-d H:i:s',$date_now_id);    $qry = "SELECT                *            FROM                notification            WHERE                send_date='". $date_now ."'                AND is_send=0            ORDER BY created ASC LIMIT 30	";    $q = mysql_query($qry);    while($r=mysql_fetch_array($q))    {        //Store ke INBOX        $sql_inbox = "INSERT INTO `inbox` (`id`, `user_id`, `subject`, `message`, `created`) VALUES (NULL, ".$r['user_id'].", '".$r['subject']."', '".$r['message']."', '".$date_now_id."');";        mysql_query($sql_inbox);                //Update ststus notification        $sql_notification = "UPDATE `notification` SET `is_send` = 1 WHERE `id` = ".$r['id'].";";        mysql_query($sql_notification);                //Send mail ke user        $message = '                    <p>Hi '.$r['user_name'].',</p>                    <p>'. nl2br($r['message']) .'</p>                    <p>Terima kasih,<br/>@koncobejo</p>                    ';        $data = array(            'to' => $r['user_email'],            'subject' => $r['subject'],            'message' => $message        );        send_mail($data);    }?>