<?php    /*     * Setiap hari minggu siang, untuk user yang sudah memiliki 20 coin, tapi belum pernah ikut lelang.     * Data di store ke table notification untuk memudahkan dalam pengitimak ke user     */    // configuration file    require_once './config.php';    //Tanggal notifikasi harus dikirim ke user dan dimasukan ke inbox    $date_now = date('Y-m-d');        //Tanggal now indonesia    $date_now_id = date('Y-m-d H:i:s');    $date_now_id = strtotime('+7 hours', strtotime($date_now_id));    $date_now_id = date('Y-m-d H:i:s',$date_now_id);        $subject = "LELANG";    $meesage = "Koin Konco Bejo saat ini sudah cukup untuk mengikuti LELANG.";    $qry = "SELECT * FROM (SELECT                user_accounts.*,                (select count(id) from bidding where user_id=user_accounts.id) as total_bid            FROM                user_accounts            WHERE                last_points >= 20            ORDER BY created ASC) as c WHERE c.total_bid=0	";    $q = mysql_query($qry);    while($r=mysql_fetch_array($q))    {        //Store ke notification        $sql_notification = "INSERT INTO `notification` "                . "("                    . "`id`, "                    . "`user_id`, "                    . "`user_email`, "                    . "`user_name`, "                    . "`subject`, "                    . "`message`, "                    . "`send_date`, "                    . "`created`"                . ") "                . "VALUES "                . "("                    . "NULL, "                    . "".$r['id'].", "                    . "'".$r['email']."', "                    . "'".$r['name']."', "                    . "'".$subject."',"                    . "'".$meesage."',"                    . "'".$date_now."',"                    . "'".$date_now_id."'"                . ");";        mysql_query($sql_notification);    }?>